SET NAMES utf8;

CREATE TABLE `blogs` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(245) DEFAULT NULL,
  `announce` text,
  `text` text,
  `create_date` datetime DEFAULT NULL,
  `publish_date` datetime DEFAULT NULL,
  `is_main` bit(1) DEFAULT b'0',
  `rating` int(11) DEFAULT '0',
  `views` int(11) DEFAULT '0',
  `comment_count` int(11) DEFAULT '0',
  `category_id` bigint(20) DEFAULT NULL,
  `is_interesting` bit(1) DEFAULT b'0',
  `is_thebest` bit(1) DEFAULT b'0',
  `account_id` bigint(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE `blog_comments` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `left_key` int(11) NOT NULL,
  `right_key` int(11) NOT NULL,
  `level` int(11) NOT NULL,
  `delete` bit(1) NOT NULL,
  `create_date` datetime NOT NULL,
  `account_id` bigint(20) NOT NULL,
  `text` text NOT NULL,
  `parent_id` bigint(20) DEFAULT NULL,
  `blog_id` bigint(20) NOT NULL,
  PRIMARY KEY (`id`) 
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE `blog_marks` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `account_id` bigint(20) NOT NULL,
  `blog_id` bigint(20) NOT NULL,
  `mark` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE `blog_photos` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `thumbnail` varchar(100) DEFAULT NULL,
  `normal` varchar(100) DEFAULT NULL,
  `blog_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE `blog_categories` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `key` varchar(64) DEFAULT NULL,
  `title` varchar(64) DEFAULT NULL,
  `order_id` int(11) DEFAULT '0',
  `is_sub` bit(1) DEFAULT b'0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

CREATE TABLE `blog_lastviews` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `blog_id` bigint(20) NOT NULL,
  `account_id` bigint(20) NOT NULL,
  `view_date` datetime DEFAULT NULL,
PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;



INSERT INTO `blogs` (`title`,`announce`,`text`,`create_date`,`publish_date`,`is_main`,`rating`,`views`,`comment_count`,`category_id`,`is_interesting`,`is_thebest`,`account_id`)  VALUES ('Представление о нем как об олигархе ушло в прошлое', '<p>\r\n	У экс-владельца ЮКОСа Михаила Ходорковского, почти 10 лет находящегося в местах лишения свободы, в среду юбилей.</p>\r\n', '<p>\r\n	Самому знаменитому российскому сидельцу исполнилось 50 лет. &laquo;Газета.Ru&raquo; спросила друзей и врагов &laquo;узника номер 1&raquo;, изменилось ли у них отношение к Ходорковскому с момента его задержания в 2003 году и что они думают о возможности открытия третьего уголовного дела против экс-руководителей ЮКОСа.</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://img.gazeta.ru/files3/893/5393893/upload-01-pic668-668x444-57822.jpg\" style=\"height: 266px; width: 500px;\" /><br />\r\n	<br />\r\n	<br />\r\n	Алексей Мельников, член федерального политбюро партии &laquo;Яблоко&raquo;<br />\r\n	<br />\r\n	&mdash; Отношение изменилось в лучшую сторону, потому что история Ходорковского &mdash;<br />\r\n	<br />\r\n	это уже не политическая и не экономическая история, а человеческая.</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://img.gazeta.ru/files3/925/5393925/upload-08-pic4_zoom-1000x1000-37757.jpg\" style=\"height: 377px; width: 600px;\" /><br />\r\n	<br />\r\n	Когда в день юбилея человека государственные телеканалы показывают про него какое-то пропагандистское кино (фильм НТВ &laquo;ЧП. Расследование: убийство в подарок&raquo;, посвященный 15-летию гибели мэра Нефтеюганска Владимира Петухова. &mdash; &laquo;Газета.Ru&raquo;) в духе того, какой он плохой, идут DDoS-атаки на его сайт, становится все понятно про нынешнюю систему. Для нее не важно, прав человек или виноват, ее задача &mdash; втоптать его в грязь. По-человечески Ходорковский, который в этих обстоятельствах находится 10 лет, вызывает на этом фоне уважение.</p>\r\n<p>\r\n	Ирина Ясина, правозащитник, в прошлом &mdash; директор программ фонда &laquo;Открытая Россия&raquo;, организованного Ходорковским</p>\r\n<p>\r\n	<img alt=\"\" src=\"http://img.gazeta.ru/files3/913/5393913/upload-06-pic4_zoom-1000x1000-38527.jpg\" style=\"height: 459px; width: 600px;\" /><br />\r\n	<br />\r\n	<br />\r\n	&mdash; Конечно, изменилось. Из жесткого начальника, которым Ходорковский некогда был для меня, он превратился в практически родного человека, из довольно механического человека, человека-функции он стал очень теплым и близким, я о нем думаю почти каждый день&hellip; Что касается возможности открытия третьего уголовного дела против него и Платона Лебедева, я отрицаю саму такую возможность. Это дело можно только высосать из пальца, и то будет непросто.<br />\r\n	<br />\r\n	Павел Данилин, глава Центра политического анализа при ИТАР-ТАСС, экс-сотрудник Фонда эффективной политики<br />\r\n	<br />\r\n	&mdash; У меня, конечно, ухудшилось отношение, я раньше многого о нем не знал. Когда я писал о нем аналитическую записку некоторое время назад, я узнал, что он подонок, мошенник, руководитель преступной банды. Каждый, кто знает правду, к нему так относится. Третье дело против Ходорковского мне безразлично &mdash; свой срок он уже получил.<br />\r\n	<br />\r\n	Геннадий Гудков, экс-депутат Госдумы, член Координационного совета оппозиции<br />\r\n	<br />\r\n	&mdash; Конечно, поменялось отношение &mdash; из нейтрально-доброжелательного и сочувственного оно превратилось в представление о нем как о серьезном политике, общественном деятеле, философе, человеке, который способен мужественно держать удар. Конечно, я стал его гораздо больше уважать, чем 10 лет назад.<br />\r\n	<br />\r\n	Мне кажется, что нынешний Ходорковский &mdash; это гораздо более глубокий, нравственный, моральный человек, чем тот, который был задержан почти 10 лет назад.<br />\r\n	<br />\r\n	То, что он не обозлился, не пылает ненавистью к своим мучителям, заставляет только больше уважать его. В этот день я желаю ему скорейшего освобождения и воссоединения со своей семьей.<br />\r\n	<br />\r\n	Вадим Соловьев, депутат Госдумы от КПРФ, глава юридической службы компартии<br />\r\n	<br />\r\n	&mdash; Тень уважения к этому человеку у меня есть, потому что он пошел за свои взгляды, убеждения в места не столь отдаленные.<br />\r\n	<br />\r\n	Другое дело &mdash; что сами либеральные идеи, как нам кажется, менее эффективны для России, чем плановая экономика, но с точки зрения личностной он вызывает определенное уважение.<br />\r\n	<br />\r\n	Вячеслав Тимченко, председатель думского комитета по вопросам местного самоуправления, член генерального совета партии &laquo;Единая Россия&raquo;<br />\r\n	<br />\r\n	&mdash; Вряд ли как-то менялось и могло измениться в принципе, потому что ответственность бизнесмена перед государством не меняется с течением времени. Что касается новых уголовных дел, то слухи я не комментирую, но напомню, что у надзорных органов есть право проводить проверки, а принимать окончательное решение &mdash; это прерогатива суда.<br />\r\n	<br />\r\n	Валерий Зубов, депутат Госдумы, экс-губернатор Красноярского края<br />\r\n	<br />\r\n	&mdash; Отношение сместилось в положительную сторону. Он очень достойно и по-человечески ведет себя, и<br />\r\n	<br />\r\n	представление о нем как об олигархе ушло в прошлое. Возникает чувство неловкости, ощущение неправильности того, что он и Лебедев сидят вот уже 10 лет &mdash; лучших лет жизни.<br />\r\n	<br />\r\n	Не я один считаю, что это неправильно. Что касается третьего уголовного дела, если его возбудят, все поймут, что оно притянуто за уши. Даже по второму делу уже многие экономисты высказывали мнение, что оно придумано, а нынешнее муссирование вопроса властью вокруг возможности возбудить новое уголовное дело против Ходорковоскго и Лебедева вообще уже находится за гранью морали и права.<br />\r\n	<br />\r\n	Алексей Чеснаков, политолог, экс-замсекретаря президиума генерального совета &laquo;Единой России&raquo;<br />\r\n	<br />\r\n	&mdash; Вообще личное отношение к людям за 10 лет не может не поменяться. Но гораздо более важным кажется то, что<br />\r\n	<br />\r\n	Ходорковский за это время не изменил своим принципам. Таких людей всегда приятно иметь как среди друзей, так и среди врагов.<br />\r\n	<br />\r\n	Владимир Соловьев, телеведущий<br />\r\n	<br />\r\n	&mdash; Во-первых, на Руси принято жалеть сидельцев. Я был в свое время неплохо знаком с деятельностью его структур, и он не являлся, конечно, героем моего романа. Но ведь тюрьма меняет людей, если верить образцам русской литературы. Во-вторых, никому никогда не пожелаешь сидеть в тюрьме, и сидеть долго. В-третьих, мне, как и многим людям, хочется, чтобы история Ходорковского имела свое разумное завершение.<br />\r\n	<br />\r\n	Третье дело против Ходорковского мне пока кажется надуманной историей, тем более что люди столько лет сидят.<br />\r\n	<br />\r\n	Можно открыть против него еще и четвертое, и пятое уголовные дела, но должен быть какой-то логичный предел. Лучше ведь ужасный конец, чем бесконечный ужас. Добавлю еще, что ругать человека в его день рождения вообще не принято.</p>\r\n', now(), now(), '', '0', '0', '0', '1','','',(select  MAX(id) from accounts));
INSERT INTO `blogs` (`title`,`announce`,`text`,`create_date`,`publish_date`,`is_main`,`rating`,`views`,`comment_count`,`category_id`,`is_interesting`,`is_thebest`,`account_id`)  VALUES ('Альтернатива не проходит через фильтр', '<p>\r\n	&laquo;Газета.Ru&raquo; выяснила, почему предвыборная кампания в Подмосковье пройдет по безальтернативному сценарию, несмотря на проблемы власти в регионе</p>\r\n', '<p>\r\n	<img alt=\"\" src=\"http://img.gazeta.ru/files3/445/5393445/andrew-pic510-510x340-78105.jpg\" style=\"width: 509px; height: 340px;\" /><br />\r\n	&nbsp;</p>\r\n<p>\r\n	В среду о начале сбора подписей для регистрации в качестве кандидата от &laquo;Яблока&raquo; на выборах губернатора Подмосковья сообщил экс-&laquo;эсер&raquo; Геннадий Гудков, объявивший себя единственной альтернативой врио главы региона Андрею Воробьеву. &laquo;Газета.Ru&raquo; выяснила, что муниципальный фильтр в Подмосковье свел на нет возможность конкурентной предвыборной борьбы. Выборы, скорее всего, пройдут по безальтернативному сценарию, несмотря на потенциальное наличие политической альтернативы на старте кампании. Ее результат может оказаться непредсказуемым разве что из-за возможных лесных пожаров в августе.<br />\r\n	<br />\r\n	Пока в Москве кипят предвыборные страсти, Подмосковье, которое в день единого голосования 8 сентября будет выбирать себе нового главу, пребывает, похоже, в летаргическом сне.<br />\r\n	<br />\r\n	О том, что в области пройдут выборы, напоминают лишь периодически мелькающие возле основных магистралей рекламные щиты &laquo;Инвестируйте в Подмосковье &ndash; инвестируйте в будущее&raquo;, &laquo;Инвестируйте в Подмосковье &ndash; гарантия губернатора&raquo;. На плакатах адрес электронной почты врио главы региона Андрея Воробьева, назначенного на этот пост в ноябре 2012 года. (Его предшественника Сергея Шойгу Кремль неожиданно перебросил в Минобороны.)<br />\r\n	<br />\r\n	Впрочем, изредка по дороге попадаются синие щиты с желтой аббревиатурой ЛДПР; другие партии и кандидаты особо себя не проявляют.<br />\r\n	<br />\r\n	В среду напомнил о себе политик некогда федерального масштаба &mdash; бывший &laquo;эсер&raquo; и экс-депутат Госдумы Геннадий Гудков. Кандидат в губернаторы Подмосковья, официально выдвинутый на этой неделе от партии &laquo;Яблоко&raquo;, начал сбор подписей муниципальных депутатов в свою поддержку, распространив обращение с просьбой поддержать его и сделать выборы конкурентными, чтобы единоросс Андрей Воробьев не стал фактически безальтернативным кандидатом.<br />\r\n	<br />\r\n	Территория парадокса<br />\r\n	<br />\r\n	Кампания в Подмосковье отличается некоторой парадоксальностью. С одной стороны, все опрошенные фигуранты выборов соглашаются, что у Воробьева неплохой рейтинг (по данным майского опроса ВЦИОМ, ему доверяют 47% опрошенных; еще в феврале было 32%), при этом они отмечают, что Московская область &mdash; сложный для власти регион, поэтому, при желании, оппозиция могла бы составить нынешнему врио достойную конкуренцию. Тем более что у кандидата от партии власти есть не только сильные стороны (в том числе поддержка федерального центра), но и слабые.<br />\r\n	<br />\r\n	В числе последних наличие в Подмосковье старых региональных элит, пришедших к власти еще в 90-е.<br />\r\n	<br />\r\n	&laquo;Говорить о том, что у Воробьева нет проблем, нельзя. Здешние администрации достаточно автономны: у многих есть выход на федеральную элиту, ведь в каждом районе есть заводы, дачи, земельные участки уважаемых людей; есть районы, где крайне сильны оппозиционные силы. Воробьев для таких элит кажется легковесным,<br />\r\n	<br />\r\n	особенно по сравнению с Шойгу&raquo;, &mdash; считает руководитель региональных программ Фонда развития информационной политики Александр Кынев.<br />\r\n	<br />\r\n	Как себя во время кампании будут вести старые региональные элиты &mdash; одна из главных интриг кампании, отмечает Кынев.<br />\r\n	<br />\r\n	&laquo;Проблема Воробьева &ndash; это поиск общего языка с главами районов и муниципалитетов. Очевидно, что молодой Воробьев не может являться для них абсолютным авторитетом. Они его слушаются в рамках вертикали власти, но, увидь они какую-то альтернативу, ситуация могла бы развиваться непредсказуемым образом&raquo;, &mdash; считает общественный деятель из Жуковского Анатолий Баташев.<br />\r\n	<br />\r\n	Очевидно, что ручное управление регионом, которое имело место быть при Громове, потеряно, и Воробьеву, как и его предшественнику Сергею Шойгу, приходится работать в режиме &laquo;пожарной команды&raquo;, а хочется, чтобы все работало системно, считает Баташев.<br />\r\n	<br />\r\n	Что касается сильных сторон, то Воробьев достаточно интересен даже протестным группам, полагает Баташев. В отличие от администрации бывшего губернатора Громова, его команда настроена на диалог с жителями и работу с такими протестными группами. Но, с другой стороны, в выборной ситуации локальные протесты все равно имеют политическое значение и будут играть против него.<br />\r\n	<br />\r\n	&laquo;Да, у него уже есть реальные достижения, например то, что президент Владимир Путин обозначил строительство ЦКАД (кольцевой дороги в Подмосковье) одним из приоритетов при дорожном строительстве&raquo;, &mdash; добавляет Баташев.<br />\r\n	<br />\r\n	Аналитики как регионального, так и федерального уровня сходятся во мнении, что, несмотря на небезоблачную ситуацию во власти, по факту кампания в Московской области пройдет в атмосфере политической предрешенности. И проблема здесь не только в наличии жесткой вертикали власти, которая фальсифицирует итоги выборов.<br />\r\n	<br />\r\n	Первая причина &mdash; парламентские партии решили не выставлять конкурентами Воробьеву тяжеловесов. И этом при том, что в Московской области традиционно сильны коммунисты, рейтинг же &laquo;Единой России&raquo; тут невелик. На думских выборах в Подмосковье единороссы набрали от 28,7% (Московская область разделена на несколько территориальных комиссий) до 36,9%, в среднем 32,83% &mdash; это один из худших результатов по стране. Для сравнения, коммунисты в этом регионе на тех же выборах получили от 23,19% до 27,51%.<br />\r\n	<br />\r\n	Вторая причина &mdash; &laquo;муниципальный фильтр&raquo;. Наличие фильтра фактически сводит на нет возможность конкурентной кампании, отмечают эксперты.</p>\r\n', now(), now(), '', '0', '0', '0', '5','','',(select  MAX(id) from accounts));
INSERT INTO `blogs` (`title`,`announce`,`text`,`create_date`,`publish_date`,`is_main`,`rating`,`views`,`comment_count`,`category_id`,`is_interesting`,`is_thebest`,`account_id`)  VALUES ('Зачистка без организаторов и нарушений', '<p>\r\n	СПЧ созвал экстренное заседание в связи с силовым выселением движения &laquo;За права человека&raquo;</p>\r\n', '<p>\r\n	<img alt=\"\" src=\"http://img.gazeta.ru/files3/485/5394485/zapravacheloveka-pic510-510x340-35150.jpg\" style=\"width: 510px; height: 340px;\" /></p>\r\n<p>\r\n	На экстренной встрече, организованной президентским советом по правам человека в среду, представители полиции Юрий Демидов и московской прокуратуры Александр Козлов не сообщили, кто руководил выселением движения &laquo;За права человека&raquo; из офиса, и информировали о том, что проверка не выявила нарушений в действиях оперативников.<br />\r\n	<br />\r\n	Совет по правам человека при президенте России провел в среду экстренное заседание в связи с выселением сотрудников организации &laquo;За права человека&raquo; из их офиса в Москве. В заседании приняли участие представители полиции и прокуратуры &mdash; начальник Главного управления по обеспечению охраны общественного порядка и координации взаимодействия с органами исполнительной власти субъектов России МВД Юрий Демидов и первый заместитель прокурора Москвы Александр Козлов. Кроме того, присутствовал уполномоченный по правам человека Владимир Лукин, московский омбудсмен Александр Музыкантский и сам пострадавший &mdash; глава движения &laquo;За права человека&raquo; Лев Пономарев.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	Представители полиции и прокуратуры так и не смогли объяснить, кто отдал приказ зачистить офис в Малом Кисловском переулке в ночь на 22 июня. &laquo;Этот вопрос ставили Лукин и Музыкантский, но Козлов и Демидов уклонились от ответа. Они говорили не совсем то, что соответствует действительности&raquo;, &mdash; заявил &laquo;Газете.Ru&raquo; Пономарев.<br />\r\n	<br />\r\n	Более того, МВД так и не нашло нарушений в действиях полицейских при выселении возглавляемой Пономаревым организации. &laquo;По предварительной оценке, в рамках назначенной служебной проверки нарушений в действиях сотрудников полиции не установлено, за исключением недопуска уполномоченного по правам человека в РФ Владимира Лукина&raquo;, &mdash; заявил Демидов.<br />\r\n	<br />\r\n	По итогам спецоперации с участием бойцов ОМОН и сотрудников неизвестного ЧОПа в полицию поступило 19 обращений. Демидов уточнил, что по обращениям продолжается процессуальная проверка. Как пояснил Пономарев, Демидов обещал расследовать только факт избиения правозащитников.<br />\r\n	<br />\r\n	В ночь на субботу, в 2.30 мск, в офис движения &laquo;За права человека&raquo; (ЗПЧ) в Малом Кисловском переулке ворвались бойцы ОМОН в камуфляже и бронежилетах. Они бросали людей на пол и волокли по коридорам, на улице задержанных передавали сотрудникам неизвестного ЧОПа. В организации Пономарева отмечают, что даже люди, согласившиеся выйти из офиса самостоятельно, были схвачены таким же жестким образам. Все это фиксировала на камеру съемочная группа телекомпании НТВ.<br />\r\n	<br />\r\n	Силовой акцией руководили люди, представившиеся Вергиным и Гордеевым, однако выполнить просьбу Музыкантского предъявить документы они отказались. В ходе штурма были избиты Пономарев, другие сотрудники офиса и кандидат в мэры Москвы, председатель партии &laquo;Яблоко&raquo; Сергей Митрохин. Последнему полиция пообещала вернуть планшетник после завершения служебной проверки.<br />\r\n	<br />\r\n	В департаменте имущества Москвы пояснили, что действия силовиков вызваны истечением срока действия договора об аренде. Организатором зачистки офиса, по данным правозащитников, был начальник отдела департамента земельных ресурсов города Москвы А. Руднев. В свою очередь, Пономарев назвал происходящее &laquo;рейдерским захватом&raquo;, отметив, что аренда офиса была оплачена до конца июля. В заключении, подготовленном членом совета по правам человека Евгением Бобровом, уточняется, что процедура пролонгации договора аренды не была завершена со стороны собственника помещения.<br />\r\n	<br />\r\n	&laquo;Аренда за текущее время арендатором оплачена, а арендодателем принята&hellip; При таких обстоятельствах уведомление об отказе арендодателя от договора аренды от 15.02.2013 г. не могло быть основанием выселения&raquo;, &mdash; говорится в заключении.<br />\r\n	<br />\r\n	Глава СПЧ Михаил Федотов на заседании в среду напомнил, что полицейские отказались представиться и пропустить Лукина в офис, поэтому он предложил полицейским прослушать курс лекций о значении правозащитных институтов. &laquo;Может быть, действительно сотрудникам полиции прочитать курс о роли правозащитных институтов, о роли уполномоченного по правам человека, президентского совета по правам человека, Общественной палаты?&raquo; &mdash; сказал Федотов. Он добавил, что готов лично принимать у сотрудников правоохранительных органов экзамены и зачеты по этой теме.<br />\r\n	<br />\r\n	По мнению Федотова, представитель МВД Демидов &laquo;взял на заметку&raquo; его предложение: &laquo;С начальником Главного управления по обеспечению охраны общественного порядка МВД Юрием Демидовым мы договорились, что члены совета будут подключены к проведению служебной проверки и будут проинформированы о ее результатах&raquo;.</p>\r\n', now(), now(), '', '0', '0', '0', '5','','',(select  MAX(id) from accounts));


